import {useState, useEffect} from 'react';
import {Redirect} from 'react-router-dom';
import {Presentational as BrowseUsers} from './presentational'
import { Request_Api } from '../customHooks/requestAPI'

export const Container=()=>{
    const [users, setUsers]=useState([]);
    const [addUser, setAddUser]=useState(false);
    const { data } = Request_Api("http://localhost:3000/user/getAll", {method:"GET"});
    setUsers(data.data);
    
    const clickAddUser=()=>{
        setAddUser(true);
    };
    if (addUser) {
        return <Redirect to="/usuario" push={true} />;

    }
    return(
        <BrowseUsers rows={users} setRows={setUsers} clickAddUser={clickAddUser} />
    )
};
