import { useState, useEffect } from 'react';
import {Presentational as UserFormulario} from './presentational'
import { useFetch } from '../customHooks/requestAPI'
import { Redirect } from 'react-router-dom';

export const Container=(props)=>{
    const [dataUser, setDataUser]=useState({
        first_name:props.location.state.first_name??"",
        last_name:props.location.state.last_name??"",
        email:props.location.state.email??"",
        password:props.location.state.password??"",
        confirm_password:props.location.state.confirm_password??"",
        phone:props.location.state.phone??"",
        profile_id:props.location.state.profile_id??3,
        id:props.location.state.id??0
    });
    
    const [url, setUrl]=useState("");
    const [back, setBack]=useState(false);
    const [ response, loading, hasError] = useFetch(url,setUrl, 
        { 
            method:"POST", 
        
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body:JSON.stringify(dataUser)
        });

        useEffect(()=>{
            if(response.success){
                clearData();
            }
        },[response]);
    
        const SubmitForm= (ev)=>{
            //setting url to call api hook
            if(!dataBook.id){
                setRequestOptions({...requestOptions, method:"POST", body:JSON.stringify(dataBook)});
                setUrl("http://localhost:3000/book/add");    
            }else{
                console.log("data json->", dataBook);
                setRequestOptions({...requestOptions, method:"PUT", body:JSON.stringify(dataBook)});
                setUrl("http://localhost:3000/book/update");
            }
            
        };

    const onChangeInput=(ev)=>{
        setDataUser({...dataUser, [ev.target.id]: ev.target.value})
    };
    const backList=()=>{
        setBack(true);
    };

    return(
        <div>
        {((response || back) && 
            ((response.success || back) &&
                <Redirect to="/usuario/lista" push={true} />
                )
            )}
        <UserFormulario dataUser={dataUser} 
                        setDataUser={setDataUser} 
                        onChangeInput={onChangeInput} 
                        submitForm={SubmitForm}
                        backList={backList}
                        />
        </div>
    )
};
