import { useState } from 'react';
export const BrowserLibros=()=>{
    const [rows, setRows] = useState([
        {titulo: "Cáscara de nuez", autor: "Ian McEwan", editorial: "Anagrama", disponible: true}
        ,{titulo: "Nosotros en la noche", autor: "Kent Haruf", editorial: "Literatura Random House  ", disponible: true}
        ,{titulo: "Los milagros prohibidos", autor: "Alexis Ravelo", editorial: "Siruela  ", disponible: true}
        ,{titulo: "Orthodoxia", autor: "Ulises Bértolo", editorial: "Ediciones Carena", disponible: true}
        ,{titulo: "El cuento de la criada", autor: "Margaret Atwood", editorial: "Salamandra", disponible: true}
    ]);

    return(
        <div>
            <h2>LISTADO DE LIBROS</h2>
            <table>
                <thead>
                    <th>Título</th>
                    <th>Autor</th>
                    <th>Disponible</th>
                    <th>Solicitar</th>
                </thead>
                <tbody>
                    {rows.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            {/* <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td> */}
                            <td>{item.elem_one}</td>
                            <td>{item.elem_two}</td>
                            <td>{item.elem_three}</td>
                            <td></td>
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    })}
                </tbody>
            </table>
        </div>
    )
}
