import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { Grid, IconButton, Collapse, Box, Backdrop, CircularProgress, InputLabel, TableCell, TableRow } from '@material-ui/core';
import { DarkTableCell, DarkTableRow, gridMessageError, BackdropLoading }  from '../customHooks/customComponents';
import ThumbUpIcon from '@material-ui/icons/ThumbUp';
import ThumbDownIcon from '@material-ui/icons/ThumbDown';

export const Presentational=({
                            requests, 
                            userAuth,
                            onClickDenegate,
                            onClickApprove,
                            onClickDeliver,
                            error,
                            response,
                            message,
                            loading
                        })=>{
    const [rowsPerPage, setRowsPerPage] = useState(5);
    const [page, setPage] = useState(0);

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
        };
    
        const handleChangePage = (event, newPage) => {
        setPage(newPage);
        };
    return(
        <div>
            {BackdropLoading(loading)}
            <Grid container spacing={2}>
                <Grid item lg={12} md={12} sm={12} xs={12}>
                    <Typography variant="h4">
                        Solicitudes de préstamo
                    </Typography>
                </Grid>
                <Grid item lg={1} md={1} sm={"auto"} xs={"auto"}></Grid>
                <Grid item lg={10} md={10} sm={12} xs={12}>
                    <TableContainer component={Paper}>
                        <Table aria-label="collapsible table">
                            <TableHead>
                                <DarkTableRow>
                                    <DarkTableCell>{"Libro"}</DarkTableCell>
                                    <DarkTableCell align="right">{"Lector"}</DarkTableCell>
                                    <DarkTableCell align="right">{"Email"}</DarkTableCell>
                                    <DarkTableCell align="left">{"Teléfono"}</DarkTableCell>
                                    <DarkTableCell align="left">{"Fecha de Solicitud"}</DarkTableCell>
                                    <DarkTableCell align="left">{"Estatus"}</DarkTableCell>
                                    <DarkTableCell align="left">{"Fecha de Actualización"}</DarkTableCell>
                                    <DarkTableCell></DarkTableCell>
                                </DarkTableRow>
                            </TableHead>
                            <TableBody>
                            {(requests &&
                            requests.slice(page * rowsPerPage??0, page * rowsPerPage + rowsPerPage).map((item) => (
                                <DarkTableRow key={item.id}>
                                    <DarkTableCell>{item.title}</DarkTableCell>
                                    <DarkTableCell>{item.first_name} {item.last_name}</DarkTableCell>
                                    <DarkTableCell>{item.email}</DarkTableCell>
                                    <DarkTableCell>{item.phonw}</DarkTableCell>
                                    <DarkTableCell align="right">{item.REQUESTDATE}</DarkTableCell>
                                    <DarkTableCell align="right">{ (!item.BORROWDATE && !item.DENEGATEDDATE) ? "NUEVA SOLICITUD" : (item.BORROWDATE && !item.DELIVERYDATE) ? "AUTORIZADO" : ((item.DENEGATEDDATE) ? "NO AUTORIZADO" : "ENTREGADO") }
                                                </DarkTableCell>
                                    <DarkTableCell align="left">{(item.BORROWDATE && !item.DELIVERYDATE) ? item.BORROWDATE : ((item.DENEGATEDDATE) ? item.DENEGATEDDATE : item.DELIVERYDATE)}</DarkTableCell>
                                    <DarkTableCell aslign="center">
                                    { (item.REQUESTDATE && !item.BORROWDATE && !item.DENEGATEDDATE ) ? 
                                        <div>
                                            <IconButton color="primary" disabled={loading} title="Autorizar solicitud..." size="large" onClick={()=>onClickApprove(item)}>
                                                <ThumbUpIcon />
                                            </IconButton>
                                            <IconButton color="secondary" disabled={loading} title="Denegar solicitud..." size="medium" onClick={()=>onClickDenegate(item)}>
                                                <ThumbDownIcon />
                                            </IconButton>
                                        </div>
                                        : (item.BORROWDATE && !item.DELIVERYDATE) ? <button disabled={loading} onClick={()=>onClickDeliver(item)}>ENTREGADO</button> : "" }
                                    </DarkTableCell>
                                </DarkTableRow>
                            )))}
                            </TableBody>
                        
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 10, 25]}
                            component="div"
                            count={requests ? requests.length : 0 }
                            rowsPerPage={requests ? rowsPerPage : 0}
                            page={page}
                            onChangePage={handleChangePage}
                            onChangeRowsPerPage={handleChangeRowsPerPage} />
                        </TableContainer>
                </Grid>
                <Grid item lg={1} md={1} sm={"auto"} xs={"auto"}></Grid>
            </Grid>
            {/* <h2>{userAuth.profile_id === 2 ?
            "Solicitudes de préstamo"
            :
                "Mis Solicitudes Préstamos"    
        }</h2>
        {(  error  &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {message}</label>
            )}
            {(  response  &&
                (response.success===false &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {response.error}</label>
                )
            )}
            {
                    loading &&
                    <p>Cargando...</p>
                }
            <table>
                <thead>
                    <tr>
                        <th>Libro</th>
                        <th>Lector</th>
                        <th>Email</th>
                        <th>Teléfono</th>
                        <th>Fecha de Solicitud</th>
                        <th>Estatus</th>
                        <th>Fecha de Actualización</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {(requests &&
                    requests.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            <td>{item.title}</td>
                            <td>{item.first_name} {item.last_name}</td>
                            <td>{item.email}</td>
                            <td>{item.phone}</td>
                            <td>{item.REQUESTDATE}</td>
                            <td>{ (!item.BORROWDATE && !item.DENEGATEDDATE) ? "SIN ATENDER" : (item.BORROWDATE && !item.DELIVERYDATE) ? "PRESTAMO AUTORIZADO" : ((item.DENEGATEDDATE) ? "PRESTAMO NO AUTORIZADO" : "LIBRO DEVUELTO") }
                            </td>
                                <td>{(item.BORROWDATE && !item.DELIVERYDATE) ? item.BORROWDATE : ((item.DENEGATEDDATE) ? item.DENEGATEDDATE : item.DELIVERYDATE)}</td>
                            <td>{ (item.REQUESTDATE && !item.BORROWDATE && !item.DENEGATEDDATE ) ? 
                                        <div>
                                            <button disabled={loading} onClick={()=>onClickApprove(item)}>APROBAR</button> 
                                            <button disabled={loading} onClick={()=>onClickDenegate(item)}>RECHAZAR</button>
                                        </div>
                                        : (item.BORROWDATE && !item.DELIVERYDATE) ? <button disabled={loading} onClick={()=>onClickDeliver(item)}>ENTREGADO</button> : "" }</td>                            
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    }))}
                </tbody>
            </table> */}
        </div>
    )
}
