import { withStyles, makeStyles } from '@material-ui/core/styles';
import { TableRow, TableCell, Button, Grid, Box, InputLabel, TextField, Backdrop, CircularProgress } from '@material-ui/core'
import SaveIcon from '@material-ui/icons/Save'
import CancelIcon from '@material-ui/icons/Cancel';
import AddBoxIcon from '@material-ui/icons/AddBox';
import ErrorIcon from '@material-ui/icons/Error';
import DoneIcon from '@material-ui/icons/Done';
import { green } from '@material-ui/core/colors';

export const DarkTableRow = withStyles((theme) => ({
    root: {
        '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
        fontSize: 14,
        },
    },
    }))(TableRow);
export const DarkTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
        fontSize: 20,
    },
    body: {
        fontSize: 18,
    },
    }))(TableCell);
    
    const GreenButton = withStyles((theme) => ({
        root: {
          color: theme.palette.getContrastText(green[500]),
          backgroundColor: green[500],
          '&:hover': {
            backgroundColor: green[700],
          },
        },
      }))(Button);

export const ButtonSave = (text, clickEvent, disabled) => {
    return(
        <Button
        variant="contained"
        color="primary"
        size="large"
        onClick={clickEvent}
        title={text}
        disabled={disabled}
        startIcon={<SaveIcon />}
        >
        {text}
        </Button>
    )
};
export const ButtonCancel = (text, clickEvent, disabled) => {
    return(
        <Button
        variant="contained"
        color="secondary"
        size="large"
        onClick={clickEvent}
        disabled={disabled}
        title={text}
        startIcon={<CancelIcon />}
        >
        {text}
        </Button>
    )
};
export const ButtonAdd = (text, clickEvent) => {
    return(
        <GreenButton
        variant="contained"
        size="large"
        title={text}
        onClick={clickEvent}
        startIcon={<AddBoxIcon />}
        >
        {text}
        </GreenButton>
    )
};
export const gridMessageError=(message)=>{
    return (
        <Grid container>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
            <Grid item lg={4} md={8} sm={10} xs={12}>
                <Box display="flex" justifyContent="center">
                    <InputLabel error><ErrorIcon /> Error al procesar la solicitud. {message}</InputLabel>
                </Box>
            </Grid>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
        </Grid>
    )
}
export const gridMessageSuccess=(message)=>{
    const styles = makeStyles((theme)=>({
        colorGreen: {
            fontSize:20
          },
        })
        );
    const classes=styles();
    return (
        <Grid container>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
            <Grid item lg={4} md={8} sm={10} xs={12}>
                <Box display="flex" justifyContent="center">
                    <InputLabel className={classes.colorGreen} ><DoneIcon /> Solicitud procesada con éxito. {message}</InputLabel>
                </Box>
            </Grid>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
        </Grid>
    )
}
export const EditTextBinded=(id, label, value, onChangeInput, disabled, type="text")=>{
    return (<TextField
        required
        type={type}
        fullWidth
        id={id}
        label={label}
        variant="outlined"
        value={value} 
        disabled={disabled}
        onChange={(ev)=> onChangeInput(ev)}
    />)

};


export const BackdropLoading=(loading)=>{
    // const styles = makeStyles((theme)=>({
    //     backdrop: {
    //         zIndex: theme.zIndex.drawer + 1,
    //         color: '#000',
    //       },
    //     })
    //     );
    // const classes=styles();

    return (
        <Backdrop className={classes.backdrop} open={loading}>
            <CircularProgress color="inherit" />
        </Backdrop>
    )
}
