import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { Grid, IconButton, Collapse, Box, Backdrop, CircularProgress, InputLabel, TableCell, TableRow } from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import EditIcon from '@material-ui/icons/Edit';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import { DarkTableCell, DarkTableRow, gridMessageError, BackdropLoading }  from '../customHooks/customComponents';

export const Presentational=({
    requests, 
    error,
    response,
    message,
    loading
})=>{
return(
        <div>
            {BackdropLoading(loading)}
            <Grid container spacing={2}>
                <Grid item lg={12} md={12} sm={12} xs={12}>
                    <Typography variant="h4">
                        Mis Solicitudes
                    </Typography>
                </Grid>
                <Grid item lg={1} md={1} sm={"auto"} xs={"auto"}></Grid>
                <Grid item lg={10} md={10} sm={12} xs={12}>
                    <TableContainer component={Paper}>
                        <Table aria-label="collapsible table">
                            <TableHead>
                                <DarkTableRow>
                                    <DarkTableCell>{"Nombre"}</DarkTableCell>
                                    <DarkTableCell align="right">{"Libro"}</DarkTableCell>
                                    <DarkTableCell align="right">{"Fecha de Solicitud"}</DarkTableCell>
                                    <DarkTableCell align="right">{"Estatus"}</DarkTableCell>
                                    <DarkTableCell align="right">{"Fecha de ActualizaciÃ³n"}</DarkTableCell>
                                    <DarkTableCell />
                                </DarkTableRow>
                            </TableHead>
                            <TableBody>
                            {(requests &&
                            requests.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((item) => (
                                <TableRow>
                                    <TableCell>{item.first_name}</TableCell>
                                    <TableCell>{item.last_name}</TableCell>
                                    <TableCell>{item.email}</TableCell>
                                    <TableCell>{item.phone}</TableCell>
                                    <TableCell> {
                                    {
                                        1: "Administrador",
                                        2: "Bibliotecario",
                                        3: "Lector",
                                        }[item.profile_id]
                                    }
                                    </TableCell>
                                    <TableCell>
                                        <IconButton title="Editar usuario..." size="small" onClick={()=>clickEditUser(item)}>
                                            <EditIcon />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            )))}
                            </TableBody>
                        
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 10, 25]}
                            component="div"
                            count={rows ? rows.length : 0 }
                            rowsPerPage={rows ? rowsPerPage : 0}
                            page={page}
                            onChangePage={handleChangePage}
                            onChangeRowsPerPage={handleChangeRowsPerPage} />
                        </TableContainer>
                </Grid>
                <Grid item lg={1} md={1} sm={"auto"} xs={"auto"}></Grid>
            </Grid>
            <h2>
                Mis Solicitudes
            </h2>
            {(  error  &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {message}</label>
            )}
            {(  response  &&
                (response.success===false &&
                    <label style={{color:"red"}}>Error al procesar la solicitud. {response.error}</label>
                )
            )}
            {
                loading &&
                <p>Cargando...</p>
            }
        <table>
            <thead>
                <tr>
                    <th>Libro</th>
                    <th>Fecha de Solicitud</th>
                    <th>Estatus</th>
                    <th>Fecha de ActualizaciÃ³n</th>
                </tr>
            </thead>
        <tbody>
        {(requests &&
        requests.map((item, i) => {
        return [
            <tr key={i} 
            // onClick={toggleMobileOpen.bind(this, i)}>
            >
            {/* <td className="toggler">
                {item.mobile_open && <ArrowUp />}
                {!item.mobile_open && <ArrowDown />}
            </td> */}
                <td>{item.title}</td>
                <td>{item.REQUESTDATE}</td>
                <td>{ (!item.BORROWDATE && !item.DENEGATEDDATE) ? "NUEVA SOLICITUD" : (item.BORROWDATE && !item.DELIVERYDATE) ? "AUTORIZADO" : ((item.DENEGATEDDATE) ? "NO AUTORIZADO" : "ENTREGADO") }
                            </td>
                <td>{(item.BORROWDATE && !item.DELIVERYDATE) ? item.BORROWDATE : ((item.DENEGATEDDATE) ? item.DENEGATEDDATE : item.DELIVERYDATE)}</td>
            
            </tr>,

            // //This inline conditional makes it weird but this works
            // ...[item.mobile_open &&
            // <tr className="test-td">
            //     <td>...</td>
            // </tr>
            // ]
        ];
        }))}
        </tbody>
        </table>
        </div>
    )
}
