import {useEffect, useState} from "react";

export const Request_Api = (url, options)=>{
    const {data, setData}=useState(null);
    console.log("entramos a requests apis");
    useEffect(()=>{
        fetch(url, options)
        .then((response)=>response.json())
        .then((data)=> {
            setData(data);
        })
    }, []);
    
    return data;
};
