import { TextField, Grid } from '@material-ui/core'
import { ButtonSave, ButtonCancel } from '../customHooks/customComponents'

export const Presentational=(
    {
        dataBook, 
        setDataBook,
        onChangeInput,
        submitForm,
        backList,
        response,
        loading,
        error
    })=>{
return (
    <div>
        {  error &&
            <label style={{color:"red"}}>Error al procesar la solicitud. {error}</label>
        }
        {  response  &&
                (response.success===false &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {response.error}</label>
            )
        }
        <Grid container spacing={2}>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
                <Grid item lg={4} md={8} sm={10} xs={12}>
                    <TextField
                        required
                        id="title"
                        label="Título"
                        variant="outlined"
                        value={dataBook.title} 
                        disabled={loading}
                        onChange={(ev)=> onChangeInput(ev)}
                    />
                </Grid>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
                <Grid item lg={4} md={8} sm={10} xs={12}>
            <TextField
            required
            id="author"
            label="Autor"
            variant="outlined"
            value={dataBook.author} 
            disabled={loading}
            onChange={(ev)=> onChangeInput(ev)}
            />
            </Grid>
            <Grid item lg={4} md={2} sm={1} xs={"auto"}></Grid>
            <TextField
            required
            id="editorial"
            label="Editorial"
            variant="outlined"
            value={dataBook.editorial} 
            disabled={loading}
            onChange={(ev)=> onChangeInput(ev)}
            />
            <TextField
            required
            id="pages"
            label="Número de Páginas"
            variant="outlined"
            type="number"
            value={dataBook.pages} 
            disabled={loading}
            onChange={(ev)=> onChangeInput(ev)}
            />
            <TextField
            required
            id="year"
            label="Año de publicación"
            variant="outlined"
            type="number"
            value={dataBook.year} 
            disabled={loading}
            onChange={(ev)=> onChangeInput(ev)}
            />
            
            {ButtonSave("Guardar", submitForm, loading)}
            {ButtonCancel("Cancelar", backList, loading)}
        </Grid>
    </div>
);
};
