import { TextField } from '@material-ui/core'

export const Presentational=(
    {
        dataBook, 
        setDataBook,
        onChangeInput,
        submitForm,
        backList,
        response,
        loading,
        error
    })=>{
return (
    <div>
        {  error &&
            <label style={{color:"red"}}>Error al procesar la solicitud. {error}</label>
        }
        {  response  &&
                (response.success===false &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {response.error}</label>
            )
        }
        <TextField
          required
          id="title"
          label="Título"
          variant="outlined"
          value={dataBook.title} 
          disabled={loading}
          onChange={(ev)=> onChangeInput(ev)}
        />
        <TextField
          required
          id="author"
          label="Autor"
          variant="outlined"
          value={dataBook.author} 
          disabled={loading}
          onChange={(ev)=> onChangeInput(ev)}
        />
         <TextField
          required
          id="editorial"
          label="Editorial"
          variant="outlined"
          value={dataBook.editorial} 
          disabled={loading}
          onChange={(ev)=> onChangeInput(ev)}
        />
        <TextField
          required
          id="pages"
          label="Número de Páginas"
          variant="outlined"
          type="number"
          value={dataBook.pages} 
          disabled={loading}
          onChange={(ev)=> onChangeInput(ev)}
        />
        <TextField
          required
          id="year"
          label="Año de publicación"
          variant="outlined"
          type="number"
          value={dataBook.year} 
          disabled={loading}
          onChange={(ev)=> onChangeInput(ev)}
        />
        
        <button disabled={loading} onClick={submitForm}>Guardar</button>
        <button disabled={loading} onClick={backList}>Cancelar</button>
    </div>
);
};
