import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { Grid, IconButton, Collapse, Box} from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import { makeStyles } from '@material-ui/core/styles';

export const Presentational=(
            {
                rows, 
                setRows, 
                clickAddBook, 
                clickEditBook, 
                clickRequestBook,
                userDetails})=>{
        
const useRowStyles = makeStyles({
    root: {
        '& > *': {
        borderBottom: 'unset',
        },
    },
    });

function Row(props) {
    const { row } = props;
    const [open, setOpen] = useState(false);
    const classes = useRowStyles();
    
    return (
        <React.Fragment>
        <TableRow className={classes.root}>
            <TableCell>
            <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton>
            </TableCell>
            <TableCell component="th" scope="row">{row.title}</TableCell>
            <TableCell align="right">{row.author}</TableCell>
            <TableCell />
        </TableRow>
        <TableRow>
            <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
                <Box margin={1}>
                <Typography variant="h6" gutterBottom component="div">
                    Details
                </Typography>
                <Table size="small" aria-label="purchases">
                    <TableHead>
                    <TableRow>
                        <TableCell>Editorial</TableCell>
                        <TableCell align="right">No. de Páginas</TableCell>
                        <TableCell align="right">Año</TableCell>
                    </TableRow>
                    </TableHead>
                    <TableBody>
                        <TableRow key={"_"+row.id}>
                        <TableCell component="th" scope="row">
                            {row.editorial}
                        </TableCell>
                        <TableCell>{row.pages}</TableCell>
                        <TableCell align="right">{row.year}</TableCell>
                        <TableCell align="center">
                        </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
                </Box>
            </Collapse>
            </TableCell>
        </TableRow>
        </React.Fragment>
    );
    }

    return(
        <div>
            <h2>LISTADO DE LIBROS</h2>
            {(userDetails.profile_id===1 &&
                <button onClick={clickAddBook}>Agregar libro</button>
            )}


            <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                    <TableHead>
                        <TableRow>
                            <TableCell />
                            <TableCell>{"Libro"}</TableCell>
                            <TableCell align="right">{"Autor"}</TableCell>
                            <TableCell />
                        </TableRow>
                    </TableHead>
                     <TableBody>
                    {(rows &&
                    rows.map((row) => (
                        <Row key={row.name} row={row} />
                    )))}
                    </TableBody>
                   
                </Table>
                </TableContainer>

            <table>
                <thead>
                    <tr>
                        {/* <th></th> */}
                        <th>Título</th>
                        <th>Autor</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {(rows &&
                    rows.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            {/* <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td> */}
                            <td>{item.title}</td>
                            <td>{item.author}</td>
                            <td>
                            {
                                {
                                1: <button onClick={()=>clickEditBook(item)}>Editar</button>,
                                2: "",
                                3: <button onClick={()=>clickRequestBook(item)}>Solicitar Préstamo</button>,
                                }[userDetails.profile_id]
                            }
                            </td>
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    })
                    )}
                </tbody>
            </table>
        </div>
    )
}
