import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { Grid, item} from '@material-ui/core';
import { style } from '../../index.module.css';

export const Presentational=(
            {
                rows, 
                setRows, 
                clickAddBook, 
                clickEditBook, 
                clickRequestBook,
                userDetails})=>{
    return(
        <div className={style.container}>
            <h2>LISTADO DE LIBROS</h2>
            {(userDetails.profile_id===1 &&
                <button onClick={clickAddBook}>Agregar libro</button>
            )}


            <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                    <TableHead>
                        <TableRow>
                            <TableCell />
                            <TableCell>{"Libro"}</TableCell>
                            <TableCell align="right">{"Autor"}</TableCell>
                            <TableCell />
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {(rows &&
                            rows.map((item, i) => {
                                return [
                            <TableRow>
                                
                                <TableCell>{item.title}</TableCell>
                                <TableCell align="right">{item.author}</TableCell>
                                <TableCell></TableCell>
                            </TableRow>
                            ]
                            })
                        )}
                    </TableBody>
                    {/* <TableBody>
                    {rows.map((row) => (
                        <Row key={row.name} row={row} />
                    ))}
                    </TableBody> */}
                </Table>
                </TableContainer>

            <table>
                <thead>
                    <tr>
                        {/* <th></th> */}
                        <th>T√≠tulo</th>
                        <th>Autor</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {(rows &&
                    rows.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            {/* <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td> */}
                            <td>{item.title}</td>
                            <td>{item.author}</td>
                            <td>
                            {
                                {
                                1: <button onClick={()=>clickEditBook(item)}>Editar</button>,
                                2: "",
                                3: <button onClick={()=>clickRequestBook(item)}>Solicitar Pr√©stamo</button>,
                                }[userDetails.profile_id]
                            }
                            </td>
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    })
                    )}
                </tbody>
            </table>
        </div>
    )
}
