export const Presentational=({rows, setRows, clickAddBook, clickEditBook, userDetails})=>{
    return(
        <div>
            <h2>LISTADO DE LIBROS</h2>
            {(userDetails.profile_id===1 &&
                <button onClick={clickAddBook}>Agregar libro</button>
            )}
            <table>
                <thead>
                    <tr>
                        {/* <th></th> */}
                        <th>Título</th>
                        <th>Autor</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {(rows &&
                    rows.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            {/* <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td> */}
                            <td>{item.title}</td>
                            <td>{item.author}</td>
                            <td>
                            {
                                {
                                1: <button onClick={clickEditBook(this)}>Editar</button>,
                                2: "",
                                3: <button>Solicitar Préstamo</button>,
                                }[userDetails.profile_id]
                            }
                            </td>
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    })
                    )}
                </tbody>
            </table>
        </div>
    )
}
