import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { Grid, IconButton, Collapse, Box} from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import EditIcon from '@material-ui/icons/Edit';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import { DarkTableCell, DarkTableRow }  from '../customHooks/customComponents';
import { makeStyles } from '@material-ui/core/styles';
import { ButtonAdd } from '../customHooks/customComponents'

export const Presentational=(
            {
                rows, 
                setRows, 
                clickAddBook, 
                clickEditBook, 
                clickRequestBook,
                userDetails})=>{
        
const useRowStyles = makeStyles({
    root: {
        '& > *': {
        borderBottom: 'unset',
        },
    },
    });

function Row(props) {
    const { row } = props;
    const [open, setOpen] = useState(false);
    const classes = useRowStyles();
    
    return (
        <React.Fragment>
        <TableRow className={classes.root}>
            <TableCell>
            <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton>
            </TableCell>
            <TableCell component="th" scope="row">{row.title}</TableCell>
            <TableCell align="right">{row.author}</TableCell>
            <TableCell>
                {
                    {
                    1: <IconButton title="Editar libro..." size="small" onClick={()=>clickEditBook(row)}>
                            <EditIcon />
                        </IconButton>,
                    2: "",
                    3: <IconButton title="Solicitar préstamo..." size="small" onClick={()=>clickRequestBook(row)}>
                        <MenuBookIcon />
                    </IconButton>,
                    }[userDetails.profile_id]
                }
            </TableCell>
        </TableRow>
        <TableRow>
            <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
                <Box margin={1}>
                
                <Typography variant="h5">
                Details
                </Typography>
                <Grid container>
                    <Grid container item xs={2}>
                        <Typography variant="h6" >
                            Editorial
                        </Typography>
                    </Grid>
                    <Grid container item xs={2}>
                        <Typography variant="subtitle1" >
                            {row.editorial}
                        </Typography>
                    </Grid>
                    <Grid container item xs={8}></Grid>
                    <Grid container item xs={2}>
                        <Typography variant="h6" >
                            Número de Páginas
                        </Typography>
                    </Grid>
                    <Grid container item xs={2}>
                        <Typography variant="subtitle1" >
                            {row.pages}
                        </Typography>
                    </Grid>
                    <Grid container item xs={8}></Grid>
                    <Grid container item xs={2}>
                        <Typography variant="h6" >
                            Año de publicación
                        </Typography>
                    </Grid>
                    <Grid container item xs={2}>
                        <Typography variant="subtitle1" >
                            {row.year}
                        </Typography>
                    </Grid>
                    <Grid container item xs={8}></Grid>
                </Grid>
                </Box>
            </Collapse>
            </TableCell>
        </TableRow>
        </React.Fragment>
    );
    }

    const buttonAdd = ButtonAdd("Agregar libro", clickAddBook);

    return(
        <div>
            <h2>LISTADO DE LIBROS</h2>
            {(userDetails.profile_id===1 &&
                <div my={"10ch"}>{buttonAdd}</div>
            )}


            <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                    <TableHead>
                        <DarkTableRow>
                            <DarkTableCell />
                            <DarkTableCell>{"Libro"}</DarkTableCell>
                            <DarkTableCell align="right">{"Autor"}</DarkTableCell>
                            <DarkTableCell />
                        </DarkTableRow>
                    </TableHead>
                     <TableBody>
                    {(rows &&
                    rows.map((row) => (
                        <Row key={row.name} row={row} />
                    )))}
                    </TableBody>
                   
                </Table>
                </TableContainer>
        </div>
    )
}
