export const Presentational=({
                            requests, 
                            userAuth,
                            onClickDenegate,
                            onClickApprove,
                            onClickDeliver,
                            error,
                            response,
                            message,
                            loading
                        })=>{
    return(
        <div>
            <h2>{userAuth.profile_id === 2 ?
            "Solicitudes de préstamo"
            :
                "Mis Solicitudes Préstamos"    
        }</h2>
        {(  error  &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {message}</label>
            )}
            {(  response  &&
                (response.success===false &&
                <label style={{color:"red"}}>Error al procesar la solicitud. {response.error}</label>
                )
            )}
            {
                    loading &&
                    <p>Cargando...</p>
                }
            <table>
                <thead>
                    <tr>
                        <th>Libro</th>
                        <th>Lector</th>
                        <th>Email</th>
                        <th>Teléfono</th>
                        <th>Fecha de Solicitud</th>
                        <th>Estatus</th>
                        <th>Fecha de Actualización</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {(requests &&
                    requests.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            {/* <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td> */}
                            <td>{item.title}</td>
                            <td>{item.first_name} {item.last_name}</td>
                            <td>{item.email}</td>
                            <td>{item.phone}</td>
                            <td>{item.REQUESTDATE}</td>
                            <td>{ (item.BORROWDATE && !item.DELIVERYDATE) ? "AUTORIZADO" : ((item.DENEGATEDDATE) ? "NO AUTORIZADO" : "ENTREGADO") }
                            </td>
                                <td>{(item.BORROWDATE && !item.DELIVERYDATE) ? item.BORROWDATE : ((item.DENEGATEDDATE) ? item.DENEGATEDDATE : item.DELIVERYDATE)}</td>
                            <td>{ (item.REQUESTDATE && !item.BORROWDATE && !item.DENEGATEDDATE ) ? 
                                        <div>
                                            <button disabled={loading} onClick={()=>onClickApprove(item)}>APROBAR</button> 
                                            <button disabled={loading} onClick={()=>onClickDenegate(item)}>RECHAZAR</button>
                                        </div>
                                        : (item.BORROWDATE && !item.DELIVERYDATE) ? <button disabled={loading} onClick={()=>onClickDeliver(item)}>ENTREGADO</button> : "" }</td>                            
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    }))}
                </tbody>
            </table>
        </div>
    )
}
