import {useEffect, useState} from "react";

export const Request_Api = async (url, options)=>{
    const {data, setData}=useState({});
    
    useEffect(()=>{
        doFetch();
    }, []);

    const doFetch=async()=>{
        const response = await fetch(url, options);
        const json = await response.json();
        setData(json);
    }
    
    return data;
};
