import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { Grid, IconButton, Collapse, Box} from '@material-ui/core';
import { style } from '../../index.module.css';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';

export const Presentational=(
            {
                rows, 
                setRows, 
                clickAddBook, 
                clickEditBook, 
                clickRequestBook,
                userDetails})=>{

        function Row(props) {
            const { row } = props;
            const [open, setOpen] = useState(false);
            const classes = useRowStyles();
            
            return (
                <React.Fragment>
                <TableRow className={classes.root}>
                    <TableCell>
                    <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                        {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    </IconButton>
                    </TableCell>
                    <TableCell component="th" scope="row">
                    {row.name}
                    </TableCell>
                    <TableCell align="right">{row.calories}</TableCell>
                    <TableCell align="right">{row.fat}</TableCell>
                    <TableCell align="right">{row.carbs}</TableCell>
                    <TableCell align="right">{row.protein}</TableCell>
                </TableRow>
                <TableRow>
                    <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
                    <Collapse in={open} timeout="auto" unmountOnExit>
                        <Box margin={1}>
                        <Typography variant="h6" gutterBottom component="div">
                            History
                        </Typography>
                        <Table size="small" aria-label="purchases">
                            <TableHead>
                            <TableRow>
                                <TableCell>Date</TableCell>
                                <TableCell>Customer</TableCell>
                                <TableCell align="right">Amount</TableCell>
                                <TableCell align="right">Total price ($)</TableCell>
                            </TableRow>
                            </TableHead>
                            <TableBody>
                            {row.history.map((historyRow) => (
                                <TableRow key={historyRow.date}>
                                <TableCell component="th" scope="row">
                                    {historyRow.date}
                                </TableCell>
                                <TableCell>{historyRow.customerId}</TableCell>
                                <TableCell align="right">{historyRow.amount}</TableCell>
                                <TableCell align="right">
                                    {Math.round(historyRow.amount * row.price * 100) / 100}
                                </TableCell>
                                </TableRow>
                            ))}
                            </TableBody>
                        </Table>
                        </Box>
                    </Collapse>
                    </TableCell>
                </TableRow>
                </React.Fragment>
            );
            }

    return(
        <div className={style.container}>
            <h2>LISTADO DE LIBROS</h2>
            {(userDetails.profile_id===1 &&
                <button onClick={clickAddBook}>Agregar libro</button>
            )}


            <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                    <TableHead>
                        <TableRow>
                            <TableCell />
                            <TableCell>{"Libro"}</TableCell>
                            <TableCell align="right">{"Autor"}</TableCell>
                            <TableCell />
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {(rows &&
                            rows.map((item, i) => {
                                return [
                            <TableRow>
                                <TableCell></TableCell>
                                <TableCell>{item.title}</TableCell>
                                <TableCell align="right">{item.author}</TableCell>
                                <TableCell></TableCell>
                            </TableRow>
                            ]
                            })
                        )}
                    </TableBody>
                    {/* <TableBody>
                    {rows.map((row) => (
                        <Row key={row.name} row={row} />
                    ))}
                    </TableBody> */}
                </Table>
                </TableContainer>

            <table>
                <thead>
                    <tr>
                        {/* <th></th> */}
                        <th>Título</th>
                        <th>Autor</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {(rows &&
                    rows.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            {/* <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td> */}
                            <td>{item.title}</td>
                            <td>{item.author}</td>
                            <td>
                            {
                                {
                                1: <button onClick={()=>clickEditBook(item)}>Editar</button>,
                                2: "",
                                3: <button onClick={()=>clickRequestBook(item)}>Solicitar Préstamo</button>,
                                }[userDetails.profile_id]
                            }
                            </td>
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    })
                    )}
                </tbody>
            </table>
        </div>
    )
}
