import { useState } from 'react';
export const BrowserLibros=()=>{
    const [rows, setRows] = useState([
        {titulo: "Cáscara de nuez", autor: "Ian McEwan", editorial: "Anagrama", paginas: 350, anio:2000, disponible: true}
        ,{titulo: "Nosotros en la noche", autor: "Kent Haruf", editorial: "Literatura Random House", paginas: 350, anio:2000, disponible: true}
        ,{titulo: "Los milagros prohibidos", autor: "Alexis Ravelo", editorial: "Siruela",paginas: 350, anio:2000, disponible: true}
        ,{titulo: "Orthodoxia", autor: "Ulises Bértolo", editorial: "Ediciones Carena", paginas: 350, anio:2000, disponible: true}
        ,{titulo: "El cuento de la criada", autor: "Margaret Atwood", editorial: "Salamandra", paginas: 350, anio:2000, disponible: true}
    ]);

    return(
        <div>
            <h2>LISTADO DE LIBROS</h2>
            <table>
                <thead>
                    <tr>
                        <th></th>
                        <th>Título</th>
                        <th>Autor</th>
                        <th>Disponible</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {rows.map((item, i) => {
                        return [
                            <tr key={i} 
                            // onClick={toggleMobileOpen.bind(this, i)}>
                            >
                            <td className="toggler">
                                {item.mobile_open && <ArrowUp />}
                                {!item.mobile_open && <ArrowDown />}
                            </td>
                            <td>{item.titulo}</td>
                            <td>{item.autor}</td>
                            <td>{item.disponible?"Sí":"No"}</td>
                            <td><button title=""></button></td>
                            </tr>,

                            // //This inline conditional makes it weird but this works
                            // ...[item.mobile_open &&
                            // <tr className="test-td">
                            //     <td>...</td>
                            // </tr>
                            // ]
                        ];
                    })}
                </tbody>
            </table>
        </div>
    )
}
