import { Presentational as FormLogin } from './presentational';
import { useState } from 'react';
import { useAuthDispatch, useAuthState, LoginUser } from '../context'
export function Container(props) {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
 
    const dispatch = useAuthDispatch()
    const { loading, errorMessage } = useAuthState() //read the values of loading and errorMessage from context
    console.log("dispatch", dispatch);
    console.log("errorMessage", errorMessage);
 
    const handleLogin = async (e) => {
        e.preventDefault()
 
        try {
            let response = await LoginUser(dispatch, { email, password })
                if (!response.user) return
            props.history.push('/dashboard')
        } catch (error) {
            dispatch({ type: 'LOGIN_ERROR', error: error.message });
        }
    }

    return (
        <FormLogin 
        email={email} 
        password={password}
        setEmail={setEmail}
        setPassword={setPassword}
        handleLogin={handleLogin}
        loading={false}
        errorMessage={errorMessage}
        />
    )
}
