import {useState, useEffect, useCallback} from 'react';
import {Redirect} from 'react-router-dom';
import {Presentational as BrowseUsers} from './presentational'
import { useFetch } from '../customHooks/requestAPI'

export function Container(){
    const [users, setUsers]=useState([]);
    const [addUser, setAddUser]=useState(false);
    
    const [response, loading, hasError] = useFetch("http://localhost:3000/user/getAll", {method:"GET"});
    
    const clickAddUser=()=>{
        setAddUser(true);
    };
    if (addUser) {
        return <Redirect to="/usuario" push={true} />;
    }
    return(
        <div>
        {(loading && 
            <label>Cargando...</label>
        )}
        {(response &&
            <BrowseUsers rows={response.data} clickAddUser={clickAddUser} />
        )}
        {(hasError && 
            <label>Error al procesar la solicitud</label>
        )}
        </div>
    )
};
